refsection.programlisting:
t = [0:0.1:2*%pi]';
clf
nc = 3;

subplot(2,nc,1)
plot2d(cos(t),sin(t))
xtitle("True circle")
a1 = gca();

subplot(2,nc,nc+1)
plot2d(cos(t),sin(t))
isoview

subplot(2,nc,2)
plot2d(7*cos(t),2*sin(t))
xtitle("True ellipse")
a2 = gca();

subplot(2,nc,nc+2)
plot2d(7*cos(t),2*sin(t))
isoview

// plot of a sphere using facets computed by eval3dp
deff("[x,y,z] = sph(alp,tet)",[
      "x = r*cos(alp).*cos(tet)+orig(1)*ones(tet)"; ..
      "y = r*cos(alp).*sin(tet)+orig(2)*ones(tet)"; ..
      "z = r*sin(alp)+orig(3)*ones(tet)"]);
r = 3; orig = [0 0 0];
[xx,yy,zz] = eval3dp(sph,linspace(-%pi/2,%pi/2,40),linspace(0,%pi*2,40));
subplot(2,nc,3)
plot3d(xx, yy, zz, alpha=60, flag=[color("green") 8 4])
xtitle("True sphere")
a3 = gca();
subplot(2,nc,nc+3)
plot3d(xx, yy, zz, alpha=60, flag=[color("green") 8 4])
isoview

// Switching isoview status for all axes of the current figure
isoview(gcf())

// Forcing isoview for all its axes
isoview(gcf(), "on")

// Relaxing isoview for the current axes
isoview off

// Setting isoview for top axes
isoview(gcf(),"off")
isoview([a1 a2 a3], "on")

