refsection.programlisting:
L = getlanguage();

// Translated strings
setlanguage ja;
_ Monday
setlanguage pt_BR;
_ Monday
setlanguage ru;
_ July

// Error messages:
setlanguage en;
linspace("") // to yield and display an error message
setlanguage ja;
linspace("")
setlanguage ru;
linspace("")

help linspace // the page should appear in Russian (if the help browser was closed)

// Restoring the initial language
setlanguage(L)
