refsection.programlisting:
L = getlanguage();

// Mots et expressions standard
setlanguage ja;
_ Monday
setlanguage pt_BR;
_ Monday
setlanguage ru;
_ July

// Error messages:
setlanguage en;
linspace("") // pour générer un message d'erreur
setlanguage ja;
linspace("")
setlanguage ru;
linspace("")

help linspace // la page doit apparaitre en Russe (si le navigateur a été réouvert)

// Rétablissement de la langue initiale
setlanguage(L)
